package org.spacebar.escape.util;


public class IntPair {
    private int x;

    public IntPair() {}
    
    /**
     * @return Returns the x.
     */
    public int getX() {
        return x;
    }

    /**
     * @param x
     *            The x to set.
     */
    public void setX(int x) {
        this.x = x;
    }

    /**
     * @return Returns the y.
     */
    public int getY() {
        return y;
    }

    /**
     * @param y
     *            The y to set.
     */
    public void setY(int y) {
        this.y = y;
    }

    private int y;

    public IntPair(int x, int y) {
        this.x = x;
        this.y = y;
    }
}